import { useState } from 'react';
import axios from 'axios';
import './App.css';

const PROFILE_IMG_URL = 'http://ddragon.leagueoflegends.com/cdn/13.1.1/img/profileicon/';
const CHAMP_IMG_URL = 'http://ddragon.leagueoflegends.com/cdn/13.1.1/img/champion/';

function App() {
  const [searchText, setSearchText] = useState("");
  const [gameList, setGameList] = useState([]);

  const [champSearch, setChampSearch] = useState("");
  const [champData, setChampData] = useState([]);

  function getPlayerGames(event) {
    axios.get("http://localhost:4000/past5Games", { params: { username: searchText}})
      .then(function (response) {
        setGameList(response.data);
      }).catch(function (error) {
        console.log(error);
      });
  }

  function getChampData(event) {
    axios.get("http://localhost:4000/champInfo", { params: { champName: champSearch}})
      .then(function (response) {
        setChampData(response.data);
      }).catch(function (error) {
        console.log(error);
      });
  }

  //console.log(gameList);
  console.log(champData);

  return (
    <div className="App">
      <h2>League Proxy Server App</h2>
      <input type="text" onChange={e => setSearchText(e.target.value)}></input> <br></br>
      <button onClick={getPlayerGames}>Get the past 5 games from your player</button>
      {gameList.length !== 0 ?
        <>
          {
              gameList.map((gameData, index) =>
                <>
                  <h2>Game {index + 1}</h2>
                  <div className="player-info">
                    {gameData.info.participants.map((data, participantIndex) => 
                      <div style={{display: "flex", alignItems: "center", justifyContent: "center"}}>
                        <p>PLAYER {participantIndex + 1}: {data.summonerName},
                        KDA: {data.kills} / {data.deaths} / {data.assists},
                        Champion: {data.championName},
                        <img style={{verticalAlign: "middle"}} height="100" width="100" src={CHAMP_IMG_URL + data.championName + ".png"}></img>
                        </p>
                      </div>
                    )}
                  </div>
                </>
              )
          }
        </>
      :
        <>
          <p>We have NO data!</p>
        </>

      }

      <input type="text" onChange={e => setChampSearch(e.target.value)}></input> <br></br>
      <button onClick={getChampData}>Get champ info</button> <br></br>
      {champData.length !== 0 ? 
        <>
          Champ Data available
        </>
      :
        <>
          No champ data available
        </>}
    </div>
  );
}

export default App;
