import { useEffect, useState } from 'react';
import axios, { all } from 'axios';
import Select from 'react-select'
import './App.css';

function App() {

	const [version, setVersion] = useState("");

	axios.get("http://localhost:4000/version")
		.then(function (response) {
			setVersion(response.data);
			console.log(version);
		}).catch(function (error) {
			console.log(error);
		});

	useEffect(() => {
		getAllChampData();
	}, []);

	//const PROFILE_IMG_URL = 'http://ddragon.leagueoflegends.com/cdn/' + version + '/img/profileicon/';
	const CHAMP_IMG_URL = 'http://ddragon.leagueoflegends.com/cdn/' + version + '/img/champion/';
	const CHAMP_SPLASH_URL = 'http://ddragon.leagueoflegends.com/cdn/img/champion/splash/';
	const SPELL_IMG_URL = 'http://ddragon.leagueoflegends.com/cdn/' + version + '/img/spell/';
	const PASSIVE_IMG_URL = 'http://ddragon.leagueoflegends.com/cdn/' + version + '/img/passive/';

	const [searchText, setSearchText] = useState("");
	const [gameList, setGameList] = useState([]);

	const [champSearch, setChampSearch] = useState("");
	const [champData, setChampData] = useState([]);
	const [allChampData, setAllChampData] = useState([]);
	const [spellData, setSpellData] = useState([]);		// Can likely make this an array for all spells, not just 1 at a time
	const [champChoice, setChampChoice] = useState(null);

	function getPlayerGames(event) {
		axios.get("http://localhost:4000/past5Games", { params: { username: searchText } })
			.then(function (response) {
				setGameList(response.data);
			}).catch(function (error) {
				console.log(error);
			});
	}

	function getSpellData() {
		axios.get("http://localhost:4000/spellInfo", { params: { champName: champSearch } })
			.then(function (response) {
				setSpellData(response.data);
			}).catch(function (error) {
				console.log(error);
			});
	}

	function getChampData(event) {
		axios.get("http://localhost:4000/champInfo", { params: { champName: champSearch } })
			.then(function (response) {
				setChampData(response.data);
				getSpellData();
				getAllChampData();
			}).catch(function (error) {
				console.log(error);
			});
	}

	function getAllChampData() {
		axios.get("http://localhost:4000/allChamps")
			.then(function (response) {
				setAllChampData(response.data);
			}).catch(function (error) {
				console.log(error);
			});
	}

	function Abilities() {
		return (
			<div style={{display: 'flex', justifyContent: 'center'}}>
			<div className='champ-spells'>
				<Passive spell={champData.passive} hasLevel={false}></Passive>
				{champData.spells.map((spell, index) =>
					<Spell spell={spell} level={1} />
				)}
			</div>
			</div>
		)
	}

	function Splash({ skin }) {
		let skinName = skin.name;
		skinName = skinName.charAt(0).toUpperCase() + skinName.slice(1); // Capitalize first letter of skin name
		return (
			<div className='splash'>
				<img src={CHAMP_SPLASH_URL + champData.name + "_" + skin.num + ".jpg"} alt="xd"></img>
				<h3> {skinName} </h3>
			</div>
		);
	}

	function Splashes() {
		return (
			<div className='splashes-div'>
				{champData.skins.map((skin) => 
					<Splash key={skin.id} skin={skin}></Splash>
				)}
			</div>
		)
	}

	function Names() {
		let champNames = Object.keys(allChampData);
		let champOptions = []
		champNames.forEach((champ) => {
			champOptions.push( {value: champ, label: champ})
		})

		const handleChange = (selectedOption) => {
			setChampChoice(selectedOption.value);
			console.log(`Option selected:`, selectedOption.value);
		};

		return <Select 
			label={'Champ Search Dropdown'}
			options={champOptions}
			//onChange={setSearchText(this)}
			onChange={handleChange}
			defaultValue={{ label: 'Select Champion'}}
			//defaultInputValue='hi'
			>
			</Select>
	}

	function Passive({ spell }) {
		return (
			<div className='spell'>
				<img height="100" width="100" src={PASSIVE_IMG_URL + spell.image.full} alt="xd"></img>
				<h3>{ spell.name }</h3>
				<h3>{ spell.description }</h3>
			</div>
		);
	}

	function Spell({ spell, level }) {
		let spell_key = "Characters/" + champData.name + "/Spells/" + spell.id + "Ability/" + spell.id;
		return (
			<div>
			<div className='spell'>
				<img height="100" width="100" src={SPELL_IMG_URL + spell.image.full} alt="xd"></img>
				<h3>{ spell.name }</h3>

				<p> CD: {spell.cooldown[level - 1]} </p>
				{spell.cost[level - 1] !== 0 ?
					<p> Cost: {spell.cost[level - 1]} </p>
				:
					<br></br>
				}
			</div>
			<div><h3>{ spell.description }</h3></div>
			</div>
		);
	}

	//console.log(gameList);
	console.log(champSearch);
	//console.log(champData);
	//console.log(champData.passive)
	//console.log(spellData);
	//console.log(allChampData);

	return (
		<div className="App">
			<h2>League of Skins</h2>

			{/* <input type="text" onChange={e => setChampSearch(e.target.value)}></input> <br></br> */}
			<Names></Names>
			<button onClick={getChampData}>Get champ info</button> <br></br>
			{/* <button onClick={getAllChampData}>Load all champ data</button> <br></br> */}



			{champData.length !== 0 ?
				<div
				// style={{ 
				// 	backgroundImage: `url(${CHAMP_SPLASH_URL + champData.name + "_" + champData.skins[0].num + ".jpg"})`,
				// 	backgroundPosition: 'center',
				// 	backgroundSize: 'cover',
				// 	backgroundRepeat: 'no-repeat',
				// 	width: '100vw',
				// 	height: '100vh',
				// 	opacity: 0.5,
				//   }}>
				>
					<h2>{champData.name + " " + champData.title}</h2>
					<img height="100" width="100" src={CHAMP_IMG_URL + champData.image.full} alt="xd"></img>

					{/* <Abilities></Abilities> */}

					{/* {champData.skins.map((skin) => 
						<Splash key={skin.id} skin={skin}></Splash>
					)} */}

					<Splashes></Splashes>

				</div>
				// <>
				// 	<select onChange={e => setChampSearch(e.target.value)}>
				// 		<option value="Select a champion"> Select a champion </option>
				// 		{Object.entries(allChampData).map(([key, value]) => {
				// 			<option value={value.name}>{value.name}</option>
				// 		})}
				// 		{champData.map((champ) => <option value={champ.name}>{champ.name}</option>)}
				// 	</select>
				// </>
				:
				<>
					No champ data available
				</>
			}
		</div>
	);
}

export default App;
